import React, {useState} from 'react';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Container from 'react-bootstrap/Container'
import '../css/CreatePostButton.css'

class CreatePostButton extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            postText: "", 
            show: "",
            setShow: "",
            handleShow: "",
            handleClose: "",
        };
        this.setModalVisibilityHandles = this.setModalVisibilityHandles.bind(this);
    }

    setModalVisibilityHandles() {
        [this.state.show, this.state.setShow] = useState(false);
        this.state.handleShow = () => this.state.setShow(true);
        this.state.handleClose = () => this.state.setShow(false);
    }

    render() {
        

        return (
            <>
            <div>
            {this.setModalVisibilityHandles()}
            </div>
            

            </>
        );
    }
}




